T= 2807function R = planet_pos(radius, theta)    R = [radius*cos(deg2rad(theta)), radius*sin(deg2rad(theta))]endfunctionfunction R = star_pos(t)  R = planet_pos(0, 0) endfunction   function R = ferengy(t)   R = planet_pos(500, 1*t) end  function R = betasoide(t)  R = planet_pos(2000, 3*t) end function R = vulcano(t)   R = planet_pos(1000, -5*t)endfunction       star = star_pos(T) planets = [ferengy(T)', betasoide(T)', vulcano(T)', ferengy(T)'] hold on; plot(star(1), star(2), 'o') plot(planets(1, :), planets(2, :), 'x-r')  hold off;